function Settings() {
    function option() {
        document.getElementById("window").style.display = "none"
    }
    function block() {
        let block = document.getElementById("window")
        block.style.display = "block"
    }
    return (
        <div className="setting">
            <h1 onClick={block}>⚙</h1>

            <div className="window" id="window">
                <optgroup onClick={option}>
                    <option>Фон</option>&nbsp;<div className="optclick">
                        <h3>Черный</h3>
                        <h3>Белый</h3>
                    </div>
                    <option>Шрифт</option>
                    <option>Размер текста</option>
                </optgroup>
            </div>
        </div>
    )
}

export default Settings
