import $ from 'jquery'
function Settings() {
    function option() {
        document.getElementById("window").style.display = "none"
    }
    function block() {
        let block = document.getElementById("window")
        block.style.display = "block"
        block.style.transition = "all 1s"
    }
    function opt_1() {
        document.getElementById("opt-1").style.display = "block"
        document.getElementById("opt-2").style.display = "none"
        document.getElementById("opt-3").style.display = "none"
        document.getElementById("opt-1").style.transition = "all 1s"
    }
    function opt_2() {
        document.getElementById("opt-2").style.display = "block"
        document.getElementById("opt-1").style.display = "none"
        document.getElementById("opt-3").style.display = "none"
        document.getElementById("opt-2").style.transition = "all 1s"
    }
    function opt_3() {
        document.getElementById("opt-3").style.display = "block"
        document.getElementById("opt-2").style.display = "none"
        document.getElementById("opt-1").style.display = "none"
        document.getElementById("opt-3").style.transition = "all 1s"
    }
    



    let body = document.getElementById("body")

    function fz_15() {
        document.getElementById("ter").style.fontSize="15px"
        body.style.fontSize="15px"
    }
    function fz_16() {
        document.getElementById("ter").style.fontSize="16px"
        body.style.fontSize="16px"
    }
    function fz_17() {
        document.getElementById("ter").style.fontSize="17px"
        body.style.fontSize="17px"
    }
    function fz_18() {
        document.getElementById("ter").style.fontSize="18px"
        body.style.fontSize="18px"
    }
    function fz_19() {
        document.getElementById("ter").style.fontSize="19px"
        body.style.fontSize="19px"
    }
    function fz_20() {
        document.getElementById("ter").style.fontSize="20px"
        body.style.fontSize="20px"
    }
    function fz_22() {
        document.getElementById("ter").style.fontSize="22px"
        body.style.fontSize="22px"
    }
    function fz_24() {
        document.getElementById("ter").style.fontSize="24px"
        body.style.fontSize="24px"
    }
    
    function fz_26() {
        document.getElementById("ter").style.fontSize="26px"
        body.style.fontSize="26px"
    }
    
    function fz_28() {
        document.getElementById("ter").style.fontSize="28px"
        body.style.fontSize="28px"
    }
function white() {
        let body = document.getElementById('body')
        document.getElementById("body").style.background = "white"
        document.getElementById("ter").style.color = "black"
        $(".span").css({
            color: "black"
        })
    }
    function black() {
        document.getElementById("body").style.background = "black"
        document.getElementById("ter").style.color = "white"
        $(".span").css({
            color: "white"
        })
    }
    return (
        <div className="setting">
            <h1 onClick={block}>⚙</h1>

            <div className="window" id="window">
                <optgroup onClick={option}></optgroup>
                    <option className="option-1" onClick={opt_1}>Фон</option>  
                    <option className="option-2" onClick={opt_2}>Шрифт</option>  
                    <option className="option-3" onClick={opt_3}>Размер текста</option>
                
                <div className="optclick opt-1" id="opt-1">
                        <h3 className="black" onClick={black}>Черный</h3>
                        <h3 className="white" onClick={white}>Белый</h3>
                    </div>
                    <div className="optclick opt-2" id="opt-2">
                        <h3>Cursive</h3>
                        <h3>'Franklin Gothic Medium', 'Arial Narrow', Arial, sans-serif</h3>
                        <h3>'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif</h3>
                        <h3>'Segoe UI', Tahoma, Geneva, Verdana, sans-serif</h3>
                        <h3>fantasy</h3>
                        <h3>sans-serif</h3>
                        <h3>serif</h3>
                        <h3>'Courier New', Courier, monospace</h3>
                    </div>
                  <div className="optclick opt-3" id="opt-3">
                        <h3 onClick={fz_15}>15px</h3>
                        <h3 onClick={fz_16}>16px</h3>
                        <h3 onClick={fz_17}>17px</h3>
                        <h3 onClick={fz_18}>18px</h3>
                        <h3 onClick={fz_19}>19px</h3>
                        <h3 onClick={fz_20}>20px</h3>
                        <h3 onClick={fz_22}>22px</h3>
                        <h3 onClick={fz_24}>24px</h3>
                        <h3 onClick={fz_26}>26px</h3>
                        <h3 onClick={fz_28}>28px</h3>
                    </div>
            </div>
        </div>
    )
}

export default Settings
