import GaleryJS from './GaleryJS.json'
import {Link, useParams} from 'react-router-dom'
import {useEffect, useState} from 'react'
import GlInfoItem from './GlInfoItem'


function GlInfo(props) {
	const { id } = useParams()
    const [glInfo, setGlInfo] = useState(null)
    useEffect(() => {
        fetch('https://raw.githubusercontent.com/ildarika/json-/main/GaleryJS.json')
        .then(res => res.json())
        .then(data => {
          setGlInfo(data)
        })
      },[]);
	const [galery, setGalery] = useState([])
    return (
            <div className="galery-info">
       <h1>{props.data.name}</h1>
           <h3 className='date'>Дата рождения: {props.data.date}</h3>
            <p className='view'>{props.data.overview}</p>
            <img src={props.data.poster_path} alt={props.data.name} className='img' />
          </div>
    )
}

export default GlInfo
