44B\u0445 \u0441\u043F\u0435\u0446\u0438\u0444\u0438\u0447\u0435\u0441\u043A\u0438\u0445 \u043A\u0430\u0442\u0435\u0433\u043E\u0440\u0438\u0439 \u043B\u0430\u043D\u0434\u0448\u0430\u0444\u0442\u043E\u0432, \u043A\u0430\u043A \u043F\u0440\u0438\u0440\u043E\u0434\u043D\u044B\u0445, \u0442\u0430\u043A \u0438 \u0432 \u0442\u043E\u0439 \u0438\u043B\u0438 \u0438\u043D\u043E\u0439 \u043C\u0435\u0440\u0435 \u0438\u0437\u043C\u0435\u043D\u0435\u043D\u043D\u044B\u0445 \u0434\u0435\u044F\u0442\u0435\u043B\u044C\u043D\u043E\u0441\u0442\u044C\u044E \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u0430: \u0433\u0435\u043E\u0445\u0438\u043C\u0438\u0447\u0435\u0441\u043A\u0438\u0439 \u041B., \u044D\u043B\u0435\u043C\u0435\u043D\u0442\u0430\u0440\u043D\u044B\u0439 \u041B., \u0430\u043D\u0442\u0440\u043E\u043F\u043E\u0433\u0435\u043D\u043D\u044B\u0439 \u041B., \u0442\u0435\u0445\u043D\u043E\u0433\u0435\u043D\u043D\u044B\u0439 \u041B., \u043A\u0443\u043B\u044C\u0442\u0443\u0440\u043D\u044B\u0439 \u041B. \u0438 \u0434\u0440. \u0413\u0435\u043E\u0433\u0440\u0430\u0444\u0438\u0447\u0435\u0441\u043A\u0438\u0439 \u043F\u043E\u044F\u0441, \u0444\u0438\u0437\u0438\u043A\u043E-\u0433\u0435\u043E\u0433\u0440\u0430\u0444\u0438\u0447\u0435\u0441\u043A\u0438\u0439 \u043F\u043E\u044F\u0441, \u043F\u0440\u0438\u0440\u043E\u0434\u043D\u044B\u0439 \u043F\u043E\u044F\u0441 \u2014 \u043D\u0430\u0438\u0431\u043E\u043B\u0435\u0435 \u043A\u0440\u0443\u043F\u043D\u043E\u0435 \u0437\u043E\u043D\u0430\u043B\u044C\u043D\u043E\u0435 \u043F\u043E\u0434\u0440\u0430\u0437\u0434\u0435\u043B\u0435\u043D\u0438\u0435 \u0433\u0435\u043E\u0433\u0440\u0430\u0444\u0438\u0447\u0435\u0441\u043A\u043E\u0439 \u043E\u0431\u043E\u043B\u043E\u0447\u043A\u0438, \u0445\u0430\u0440\u0430\u043A\u0442\u0435\u0440\u0438\u0437\u0443\u044E\u0449\u0435\u0435\u0441\u044F \u043E\u0431\u0449\u0438\u043C\u0438 \u043E\u0441\u043E\u0431\u0435\u043D\u043D\u043E\u0441\u0442\u044F\u043C\u0438 \u0440\u0435\u0436\u0438\u043C\u0430 \u0442\u0435\u043F\u043B\u0430 \u0438 \u0432\u043B\u0430\u0433\u0438, \u0446\u0438\u0440\u043A\u0443\u043B\u044F\u0446\u0438\u0438 \u0432\u043E\u0437\u0434\u0443\u0448\u043D\u044B\u0445 \u043C\u0430\u0441\u0441, \u0430 \u043D\u0430 \u0441\u0443\u0448\u0435 \u0435\u0449\u0451 \u0438 \u0441\u0432\u043E\u0435\u043E\u0431\u0440\u0430\u0437\u0438\u0435\u043C \u0431\u0438\u043E\u0433\u0435\u043E\u0445\u0438\u043C\u0438\u0447\u0435\u0441\u043A\u0438\u0445, \u0433\u0435\u043E\u043C\u043E\u0440\u0444\u043E\u043B\u043E\u0433\u0438\u0447\u0435\u0441\u043A\u0438\u0445 \u0438 \u0434\u0440. \u043F\u0440\u043E\u0446\u0435\u0441\u0441\u043E\u0432, \u0441\u043E\u0441\u0442\u0430\u0432\u043E\u043C \u043F\u043E\u0447\u0432, \u0440\u0430\u0441\u0442\u0438\u0442\u0435\u043B\u044C\u043D\u043E\u0433\u043E \u043F\u043E\u043A\u0440\u043E\u0432\u0430 \u0438 \u0436\u0438\u0432\u043E\u0442\u043D\u043E\u0433\u043E \u043C\u0438\u0440\u0430. \u041F\u043E\u044F\u0441\u0430 \u043D\u0430 \u0441\u0443\u0448\u0435 \u0432 \u0437\u0430\u0432\u0438\u0441\u0438\u043C\u043E\u0441\u0442\u0438 \u043E\u0442 \u0438\u0437\u043C\u0435\u043D\u0435\u043D\u0438\u044F \u043A\u043B\u0438\u043C\u0430\u0442\u0438\u0447\u0435\u0441\u043A\u0438\u0445 \u0444\u0430\u043A\u0442\u043E\u0440\u043E\u0432 \u043F\u043E\u0434\u0440\u0430\u0437\u0434\u0435\u043B\u044F\u044E\u0442\u0441\u044F \u043D\u0430 \u043E\u0441\u043D\u043E\u0432\u043D\u044B\u0435 \u0435\u0434\u0438\u043D\u0438\u0446\u044B \u0437\u043E\u043D\u0430\u043B\u044C\u043D\u043E\u0441\u0442\u0438 \u2014 \u0433\u0435\u043E\u0433\u0440\u0430\u0444\u0438\u0447\u0435\u0441\u043A\u0438\u0435 \u0437\u043E\u043D\u044B.", /*#__PURE__*/_jsxDEV("br", {}, void 0, false, {
